import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { AlertCircle, Check, Send } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import { Alert, AlertDescription, AlertTitle } from './components/ui/Alert';

const EC = require('elliptic').ec;
const ec = new EC('secp256k1');

const WebSocketServer = 'ws://localhost:8080';

const App = () => {
  const [wallet, setWallet] = useState(null);
  const [recipientWallet, setRecipientWallet] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [blockchain, setBlockchain] = useState([]);
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [logs, setLogs] = useState([]);
  const [ws, setWs] = useState(null);
  const [balance, setBalance] = useState(10); // Starting balance for sender
  const [recipientBalance, setRecipientBalance] = useState(5); // Starting balance for recipient

  useEffect(() => {
    let socket = new WebSocket(WebSocketServer);
    setWs(socket);

    socket.onopen = () => {
      console.log('WebSocket connection established');
      addLog('Connected to server');
    };

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Received data from server:', data);
      handleSocketMessage(data);
    };

    socket.onerror = (error) => {
      console.error('WebSocket error:', error);
      addLog('WebSocket error', 'error');
    };

    socket.onclose = () => {
      console.log('WebSocket connection closed');
      addLog('Disconnected from server', 'error');

      // Attempt to reconnect after a delay
      setTimeout(() => {
        console.log('Reconnecting to WebSocket...');
        socket = new WebSocket(WebSocketServer);
        setWs(socket);
      }, 1000); // 1 second delay before reconnecting
    };

    return () => {
      socket.close();
    };
  }, []);

  const handleSocketMessage = (data) => {
    console.log('Received data from server:', data);
    switch (data.type) {
      case 'NEW_PENDING_TRANSACTION':
        addLog('New pending transaction received');
        setTransactions(prev => [...prev, data.transaction]);
        break;
      case 'NEW_BLOCK':
        addLog('New block mined and added to the blockchain');
        setBlockchain(prev => [...prev, data.block]);
        updateBalances();
        addLog('Transaction confirmed and block added to blockchain', 'success');
        break;
      case 'BLOCKCHAIN':
        setBlockchain(data.chain);
        break;
      case 'ERROR':
        addLog(`Error: ${data.message}`, 'error');
        break;
    }
  };

  const addLog = (message, type = 'info') => {
    setLogs(prev => [...prev, { id: uuidv4(), message, type, timestamp: new Date() }]);
  };

  const createWallet = () => {
    const key = ec.genKeyPair();
    const publicKey = key.getPublic('hex');
    const privateKey = key.getPrivate('hex');
    setWallet({ publicKey, privateKey });
    addLog('New wallet created');

    // Create a recipient wallet with predefined keys and balance
    const recipientKey = ec.genKeyPair();
    const recipientPublicKey = recipientKey.getPublic('hex');
    const recipientPrivateKey = recipientKey.getPrivate('hex');
    setRecipientWallet({ publicKey: recipientPublicKey, privateKey: recipientPrivateKey });
    setRecipient(recipientPublicKey); // Automatically set recipient in UI
    addLog('Recipient wallet created');
  };

  const getUTXOs = () => {
    // In a real-world application, UTXOs would be fetched from the server.
    // For this simulation, we'll mock UTXOs based on current balances.
    return [
      { txId: 'txid1', amount: balance / 2 }, // Split current balance into two UTXOs
      { txId: 'txid2', amount: balance / 2 }
    ];
  };

  const sendTransaction = () => {
    if (!wallet || !recipientWallet) {
      addLog('Please create a wallet first', 'error');
      return;
    }

    const utxos = getUTXOs();
    let totalInput = 0;
    let inputs = [];

    for (const utxo of utxos) {
      totalInput += utxo.amount;
      inputs.push(utxo.txId);
      if (totalInput >= parseFloat(amount)) break;
    }

    if (totalInput < parseFloat(amount)) {
      addLog('Insufficient balance', 'error');
      return;
    }

    addLog('Creating transaction...');

    // Ensure the amount is correctly parsed as a float
    const parsedAmount = parseFloat(amount);
    if (isNaN(parsedAmount) || parsedAmount <= 0) {
      addLog('Invalid amount', 'error');
      return;
    }

    const transaction = {
      type: 'NEW_TRANSACTION',
      fromAddress: wallet.publicKey,
      toAddress: recipient,
      amount: parsedAmount,  // Ensure the amount is correct here
      privateKey: wallet.privateKey,
      inputs
    };

    addLog('Transaction created and signed.');
    addLog('Broadcasting transaction to the network...');

    ws.send(JSON.stringify(transaction));

    addLog('Transaction broadcasted.');
    setRecipient('');
    setAmount('');
  };

  const mineBlock = () => {
    if (!wallet) {
      addLog('Please create a wallet first', 'error');
      return;
    }

    addLog('Starting mining process...');
    ws.send(JSON.stringify({ type: 'MINE_BLOCK', miningRewardAddress: wallet.publicKey }));
    addLog('Mining new block...');
  };

  const updateBalances = () => {
    // Update balances after the transaction is confirmed
    const sentAmount = parseFloat(amount);
    setBalance(prev => prev - sentAmount);
    setRecipientBalance(prev => prev + sentAmount);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-4xl font-bold mb-8 text-center text-blue-600">Simplified Bitcoin-like System</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold mb-4">Wallet</h2>
          {wallet ? (
            <div>
              <p className="mb-2"><strong>Public Key:</strong> {wallet.publicKey.substring(0, 20)}...</p>
              <p className="mb-4"><strong>Private Key:</strong> {wallet.privateKey.substring(0, 20)}...</p>
              <p className="mb-4"><strong>Balance:</strong> {balance} BTC</p>
            </div>
          ) : (
            <button onClick={createWallet} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors">
              Create Wallet
            </button>
          )}
        </div>

        {recipientWallet && (
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold mb-4">Recipient Wallet</h2>
            <div>
              <p className="mb-2"><strong>Public Key:</strong> {recipientWallet.publicKey.substring(0, 20)}...</p>
              <p className="mb-4"><strong>Private Key:</strong> {recipientWallet.privateKey.substring(0, 20)}...</p>
              <p className="mb-4"><strong>Balance:</strong> {recipientBalance} BTC</p>
            </div>
          </div>
        )}
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md mt-8">
        <h2 className="text-2xl font-semibold mb-4">Send Transaction</h2>
        <input
          type="text"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
          placeholder="Recipient Address"
          className="w-full p-2 mb-2 border rounded"
        />
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
          className="w-full p-2 mb-4 border rounded"
        />
        <button onClick={sendTransaction} className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors">
          <Send className="inline-block mr-2" size={16} />
          Send Transaction
        </button>
      </div>

      <div className="mt-8">
        <h2 className="text-2xl font-semibold mb-4">Blockchain Visualization</h2>
        <LineChart width={600} height={300} data={blockchain} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="index" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="transactions.length" stroke="#8884d8" name="Transactions" />
          <Line type="monotone" dataKey="nonce" stroke="#82ca9d" name="Nonce" />
        </LineChart>
      </div>

      <div className="mt-8">
        <h2 className="text-2xl font-semibold mb-4">Mining</h2>
        <button onClick={mineBlock} className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600 transition-colors">
          Mine New Block
        </button>
      </div>

      <div className="mt-8">
        <h2 className="text-2xl font-semibold mb-4">Activity Log</h2>
        <div className="space-y-2">
          {logs.map((log) => (
            <Alert key={log.id} variant={log.type === 'error' ? 'destructive' : 'default'}>
              {log.type === 'error' ? <AlertCircle className="h-4 w-4" /> : <Check className="h-4 w-4" />}
              <AlertTitle>{new Date(log.timestamp).toLocaleTimeString()}</AlertTitle>
              <AlertDescription>{log.message}</AlertDescription>
            </Alert>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
